import React from "react";
import './App.css';
import './Basket.css';
import Header from "./Header";
import { Pizza } from "./Main"; // Імпортуйте інтерфейс Pizza з Main.tsx

interface BasketProps {
    pizzas: Pizza[] | undefined; // Визначте тип для пропа pizzas
}

function Basket({ pizzas }: BasketProps) {
    return (
        <div>
            <Header />
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="divBasketPizza">
                {pizzas?.map((pizza, index) => 
                    <div className="pizza1" key={index}>
                        <img src={pizza.img}/>
                        <h2>{pizza.name}</h2>
                    </div>
                )}
                </div>
            </div>
        </div>
    );
}

export default Basket;
