import React from "react";
import './App.css';
import './Basket.css';
import Header from "./Header";
import { Pizza } from "./interfaces"; // Імпортуйте інтерфейс Pizza

interface BasketProps {
    pizzas: Pizza[] | undefined; // Визначте тип для пропа pizzas
}

function Basket({ pizzas }: BasketProps) {
    return (
        <div>
            <Header />
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="divBasketPizza">
                    {pizzas?.map((pizza: Pizza, index: number) => ( // Визначте типи для pizza та index
                        <div key={index}>
                            <img src={pizza.img} alt={pizza.name} />
                            <h3>{pizza.name}</h3>
                            {/* Додайте інші властивості pizza тут */}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Basket;
