import React, {useState} from "react";
import './App.css';
import './Basket.css';
import { Pizza } from "./Main";
import { Link } from "react-router-dom";
import Modal from 'react-modal';

interface BasketProps { pizzas: Pizza[] }

function Basket({ pizzas }: BasketProps) {
    const totalPrice = pizzas.reduce((total, pizza) => total + pizza.price, 0);

    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleOpenModal = () => {
      setIsModalOpen(true);
    };

    const handleCloseModal = () => {
      setIsModalOpen(false);
    };

    return (
        <div>
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="mainDivBasketPizza">
                    {pizzas?.map((pizza, index) => (
                        <div className="BasketPizza" key={index}>
                            <img src={pizza.img} alt={pizza.name} />
                            <div>
                                <h2 className="pizzaName">{pizza.name}</h2>
                                <p className="pCmDough">{pizza.cm}, {pizza.dough} тісто</p>
                            </div>
                            <div className="divPrise"><p>{pizza.price} грн.</p></div>
                        </div>
                    ))}
                </div>

                <div className="buttons">
                    <Link to="/"><a className="aMenu">Повернутися до меню</a></Link>
                    <div>
                        <p>До сплати: {totalPrice} грн.</p>
                        <button onClick={handleOpenModal}>ЗРОБИТИ ЗАМОВЛЕННЯ</button>
                    </div>                    
                </div>
            </div>

            <Modal isOpen={isModalOpen} onRequestClose={handleCloseModal} contentLabel="Оформлення замовлення">
                <div className="modal-header">
                    <h2>Оформлення замовлення</h2>
                    <span className="close" onClick={handleCloseModal}>&times;</span>
                </div>
                <form>
                    <label htmlFor="name">Ім'я:</label>
                    <input type="text" id="name" name="name" placeholder="Ім'я" required />

                    <label htmlFor="phone">Номер телефону:</label>
                    <input type="tel" id="phone" name="phone" placeholder="Номер телефону" required />

                    <label htmlFor="address">Адреса:</label>
                    <input type="text" id="address" name="address" placeholder="Адреса" required />

                    <button type="submit">Підтвердити замовлення</button>
                </form>
            </Modal>

        </div>
    );
}

export default Basket;
