import React from "react";
import './PizzaMaker.css';
import './App.css';
import { Pizza, Dough } from './Main';

interface PizzaMakerProps { pizzas: Pizza[] }

function PizzaMaker({ pizzas }: PizzaMakerProps) {
    const doughOptions = Object.values(Dough).map((dough: string) => (
        <option key={dough} value={dough}>
          {dough}
        </option>
    ));

    return (
        <div className="mainPizzaMakers">
            <h1>Дві улюблених в одній піці</h1>
            <div>
                <div className="twoPizzas">
                    <div>
                        <h3>КРОК №1 - Оберіть першу половину піци:</h3>
                        <select className="selectDough" value={pizzas[0]?.dough || Dough.Thin}>{doughOptions}</select>
                        <h3>КРОК №1 - Оберіть першу половину піци:</h3>
                        <select className="selectDough" value={pizzas[0]?.name || ''}>{doughOptions}</select>
                    </div>
                    <div></div>
                </div>
                <div className="divMaking">
                    <div className="lineOne"></div>
                    <div className="lineTwo"></div>
                    <div className="lineTree"></div>
                </div>
            </div>
        </div>
    )
};

export default PizzaMaker;
