import React, {useState, useEffect} from "react";
import './App.css';
import baners from "./baners.png";

interface Pizza{
    img: string,
    name: string, 
    cm: number,
    dough: Dough,
    price: number,
    ingredients: string[]
}

enum Dough{
    Lush = "пишне",
    Thin = "тонке",
    HotDog = "хот-дог"
}

function Main() {

    const [pizzas, setPizzas] = useState<Pizza[]>();

    const doughOptions = Object.values(Dough).map((dough) => (
        <option key={dough} value={dough}>
          {dough}
        </option>
      ));

      useEffect(() => {

        fetch('pizza.json')
          .then((response) => response.json())
          .then((jsonData) => {
            setPizzas(jsonData);
          })
          .catch((error) => {
            console.error('Ошибка загрузки данных:', error);
          });
      }, []);

    return(
        <main>

            <div className="title">
                <h1>Меню</h1>
                <img className="twoPizzas" src={baners}/>
            </div>

            <div className="divPizza">
                    <div className="pizza1">
                        <img src="./pizza/peperoni.png"/>
                        <h2>Пепероні</h2>
                        <div className="centimeters"><p>25 см</p><p>30 см</p><p>35 см</p></div>
                        <div className="dough">
                            <p>Тісто</p>
                            <select className="selectDough">{doughOptions}</select>
                        </div>
                        <div className="order"><p>250 грн.</p><button>ЗАМОВИТИ</button></div>
                        <p>Моцарела • салямі Пепероні • томатний соус (основа)</p>
                    </div>
            </div>

        </main>
    )
}

export default Main;
