import React, {useState, useEffect} from "react";
import baners from "./baners.png";
import './App.css';

export interface Pizza{
    img: string,
    name: string, 
    cm: number,
    dough: Dough,
    price: number,
    ingredients: string[]
}

export enum Dough{
    Lush = "пишне",
    Thin = "тонке",
    HotDog = "хот-дог"
}

function Main() {

    const [pizzas, setPizzas] = useState<Pizza[]>();

    const doughOptions = Object.values(Dough).map((dough) => (
        <option key={dough} value={dough}>
          {dough}
        </option>
      ));


      const fetchPizzas = () => {
        fetch('pizza.json')
          .then((response) => response.json())
          .then((jsonData: Pizza[]) => { // Вкажіть тип даних як PizzaData[]
            setPizzas(jsonData);
          })
          .catch((error) => {
            console.error('Ошибка загрузки данных:', error);
          });
      };

      useEffect(() => {
        fetchPizzas();
      }, []);

    return(
        <main>

            <div className="title">
                <h1>Меню</h1>
                <img className="twoPizzas" src={baners}/>
            </div>            

            <div className="divPizza">
                {pizzas?.map((pizza, index) => 
                    <div className="pizza1" key={index}>
                        <img src={pizza.img}/>
                        <h2>{pizza.name}</h2>
                        <div className="centimeters"><p>{pizza.cm}</p><p>30 см</p><p>35 см</p></div>
                        <div className="dough">
                            <p>Тісто</p>
                            <select className="selectDough" value={pizza.dough}>{doughOptions}</select>
                        </div>
                        <div className="order"><p>{pizza.price} грн.</p><button>ЗАМОВИТИ</button></div>
                        <p>{pizza.ingredients.join(' • ')}</p>
                    </div>
                )}
            </div>


        </main>
    )
}

export default Main;
