import React, {useState, useEffect, useLayoutEffect} from "react";
import baners from "./baners.png";
import './App.css';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';

export interface Pizza{
    img: string,
    name: string, 
    cm: number,
    dough: Dough,
    price: number,
    ingredients: string[]
}

interface MainProps{pizzas: Pizza[]}

export enum Dough{
    Lush = "пишне",
    Thin = "тонке",
    HotDog = "хот-дог"
}

function Main({pizzas}: MainProps) {

    const doughOptions = Object.values(Dough).map((dough) => (
        <option key={dough} value={dough}>
          {dough}
        </option>
      ));

      useLayoutEffect(() => {
        window.scrollTo(0, 0)
    });

    return(
        <main>

            <div className="title">
                <h1>Меню</h1>
                <img className="twoPizzas" src={baners}/>
            </div>            

            <div className="divPizza">
                {pizzas?.map((pizza, index) => 
                    <div className="pizza1" key={index}>
                        <img src={pizza.img}/>
                        <h2>{pizza.name}</h2>
                        <div className="centimeters"><p>{pizza.cm}</p><p>30 см</p><p>35 см</p></div>
                        <div className="dough">
                            <p>Тісто</p>
                            <select className="selectDough" value={pizza.dough}>{doughOptions}</select>
                        </div>
                        <div className="order"><p>{pizza.price} грн.</p><Link to={'/basket'} className='Name'><button>ЗАМОВИТИ</button></Link></div>
                        <p>{pizza.ingredients.join(' • ')}</p>
                    </div>
                )}
            </div>


        </main>
    )
}

export default Main;
