import React from "react";
import './App.css';
import './Basket.css';
import Header from "./Header";
import { Pizza } from "./Main";
import { useEffect } from "react";

interface BasketProps {pizzas: Pizza[]}

function Basket({pizzas}: BasketProps) {
    return (
        <div>
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="mainDivBasketPizza">
                    {pizzas?.map((pizza, index) => (
                        <div className="BasketPizza" key={index}>
                            <img className="imgBasketPizza" src={pizza.img} alt={pizza.name}/>
                            <div><h2>{pizza.name}</h2><p>{pizza.cm},</p><p>{pizza.dough} тісто</p></div>
                            <div>{pizza.price}</div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Basket;
