import React, { useState, useLayoutEffect } from "react";
import './App.css';
import './Basket.css';
import { Pizza } from "./Main";
import { Link } from "react-router-dom";


interface BasketProps { pizzas: Pizza[] }

function Basket({ pizzas }: BasketProps) {
    const totalPrice = pizzas.reduce((total, pizza) => total + pizza.price, 0);
    const { register, handleSubmit, formState: { errors } } = useForm();

    useLayoutEffect(() => {
        window.scrollTo(0, 0)
    });

    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleOpenModal = () => {
        setIsModalOpen(true);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    const onSubmit = (data) => {
        console.log(data);
        handleCloseModal();
    };

    if (pizzas.length === 0) {
        return <div className="basket"><h2>Ваш кошик порожній</h2></div>;
    }

    return (
        <div>
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="mainDivBasketPizza">
                    {pizzas?.map((pizza, index) => (
                        <div className="BasketPizza" key={index}>
                            <img src={pizza.img} alt={pizza.name} />
                            <div>
                                <h2 className="pizzaName">{pizza.name}</h2>
                                <p className="pCmDough">{pizza.cm}, {pizza.dough} тісто</p>
                            </div>
                            <div className="divPrise"><p>{pizza.price} грн.</p></div>
                        </div>
                    ))}
                </div>

                <div className="buttons">
                    <Link to="/"><a className="aMenu">Повернутися до меню</a></Link>
                    <div>
                        <p>До сплати: {totalPrice} грн.</p>
                        <button onClick={handleOpenModal}>ЗРОБИТИ ЗАМОВЛЕННЯ</button>
                    </div>
                </div>
            </div>

            <Modal isOpen={isModalOpen} onRequestClose={handleCloseModal}>
                <div className="modal-header">
                    <h2>Оформлення замовлення</h2>
                    <span className="close" onClick={handleCloseModal}>&times;</span>
                </div>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="nameAndPhone">
                        <div className="divInput">
                            <label htmlFor="name">Ім'я:</label>
                            <input
                                {...register('name', { required: true, minLength: 3 })}
                                id="name"
                                placeholder="Ім'я"
                            />
                            {errors.name && <span>Це поле є обов'язковим і має містити не менше 3 символів</span>}
                        </div>

                        <div className="divInput">
                            <label htmlFor="phone">Номер телефону:</label>
                            <input
                                {...register('phone', { required: true })}
                                id="phone"
                                placeholder="Номер телефону"
                            />
                            {errors.phone && <span>Це поле є обов'язковим</span>}
                        </div>
                    </div>

                    <div className="divInput">
                        <label htmlFor="address">Адреса:</label>
                        <input
                            {...register('address', { required: true, minLength: 6 })}
                            id="address"
                            placeholder="Адреса"
                        />
                        {errors.address && <span>Це поле є обов'язковим і має містити не менше 6 символів</span>}
                    </div>

                    <button type="submit">Підтвердити замовлення</button>
                </form>
            </Modal>
        </div>
    );
}

export default Basket;
