import React, {useState, useLayoutEffect} from "react";
import './App.css';
import './Basket.css';
import { Pizza } from "./Main";
import { Link } from "react-router-dom";
import Modal from 'react-modal';

interface BasketProps {pizzas: Pizza[]}

function Basket({pizzas}: BasketProps) {
    const totalPrice = pizzas.reduce((total, pizza) => total + pizza.price, 0);

    useLayoutEffect(() => {
        window.scrollTo(0, 0)
    });

    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleOpenModal = () => {
      setIsModalOpen(true);
    };

    const handleCloseModal = () => {
      setIsModalOpen(false);
    };


    const [formData, setFormData] = useState({name: '', address: ''});

      const handleChange = (e: React.FormEvent<HTMLInputElement>) => {
        const {name, value} = e.target as HTMLInputElement;
        setFormData({
          ...formData, 
          [name]: value
        });
      }

      const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();

        if (formData.name.length < 3) {
          alert("Ім'я має містити мінімум 3 символів");
          return;
        }

        if (formData.address.length < 6) {
            alert("Адреса має містити мінімум 6 символів");
            return;
          }

        alert('Замовлення оформлено');
      }

    return (
        <div>
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="mainDivBasketPizza">
                    {pizzas?.map((pizza, index) => (
                        <div className="BasketPizza" key={index}>
                            <img src={pizza.img} alt={pizza.name} />
                            <div>
                                <h2 className="pizzaName">{pizza.name}</h2>
                                <p className="pCmDough">{pizza.cm}, {pizza.dough} тісто</p>
                            </div>
                            <div className="divPrise"><p>{pizza.price} грн.</p></div>
                        </div>
                    ))}
                </div>

                <div className="buttons">
                    <Link to="/"><a className="aMenu">Повернутися до меню</a></Link>
                    <div>
                        <p>До сплати: {totalPrice} грн.</p>
                        <button onClick={handleOpenModal}>ЗРОБИТИ ЗАМОВЛЕННЯ</button>
                    </div>                    
                </div>
            </div>

            <Modal
                style={{
                    overlay: {
                      position: 'fixed',          
                      backgroundColor: '#00000075'
                    },
                    content: {
                      position: 'absolute',                    
                      width: "130%",
                      height: "35%",
                      top: '25%',
                      left: '30%',
                      right: '40%',
                      bottom: '40px',
                      background: '#fff',
                      overflow: 'auto',
                      WebkitOverflowScrolling: 'touch',
                      borderRadius: '30px',
                      outline: 'none',
                      padding: '20px',
                      paddingBottom: '60px',
                    }
                  }} isOpen={isModalOpen} onRequestClose={handleCloseModal} contentLabel="Оформлення замовлення">
                <div className="modal-header">
                    <h2>Оформлення замовлення</h2>
                    <span className="close" onClick={handleCloseModal}>&times;</span>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="nameAndPhone">
                        <div className="divInput">
                            <label htmlFor="name">Ім'я:</label>
                            <input type="text" id="name" name="name" placeholder="Ім'я" value={formData.name} onChange={handleChange} required/>
                        </div>

                        <div className="divInput">
                            <label htmlFor="phone">Номер телефону:</label>
                            <input type="tel" id="phone" name="phone" placeholder="Номер телефону" required/>
                        </div>
                    </div>

                    <label htmlFor="address">Адреса:</label>
                    <input type="text" id="address" name="address" placeholder="Адреса" value={formData.address} onChange={handleChange} required/>

                    <button type="submit">Підтвердити замовлення</button>
                </form>
            </Modal>

        </div>
    );
}

export default Basket;
