import React from "react";
import './App.css';
import './Basket.css';
import Header from "./Header";

interface BasketProps {
    pizzas: Pizza[] | undefined;
    fetchPizzas: () => void;
}

function Basket({ pizzas, fetchPizzas }: BasketProps) {
    return (
        <div>
            <Header/>
            <div className="basket">
                <h2>Ваш кошик</h2>

                <div className="divBasketPizza">
                    {pizzas?.map((pizza, index) => 
                        <div className="pizza1" key={index}>
                            <img src={pizza.img}/>
                            <h2>{pizza.name}</h2>
                            <div className="centimeters"><p>{pizza.cm}</p><p>30 см</p><p>35 см</p></div>
                            <div className="dough">
                                <p>Тісто</p>
                                <select className="selectDough" value={pizza.dough}>{doughOptions}</select>
                            </div>
                            <div className="order"><p>{pizza.price} грн.</p><button>ЗАМОВИТИ</button></div>
                            <p>{pizza.ingredients.join(' • ')}</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Basket;
